export function doInsertionSort(array) {
    const animations = [];
    console.log(array);
    for (let i=1; i<array.length; i++) {
        let key = array[i];
        animations.push([1, i]); //color the key red
        let j = i-1;
        while (j>=0 && key<array[j]) {
            array[j + 1] = array[j];
            animations.push([2, j+1, j]); //select j+1 and j (purple)
            animations.push([3, j+1, j]); //j+1 overwritten by j (orange)
            animations.push([0, j+1, j]); //back to blue
            j-=1;
        }
        array[j+1]=key;
        // animations.push([2, j+1, i]); //select j+1 and j (purple)
        // animations.push([3, j+1, i]); //j+1 overwritten by key (orange)
        // animations.push([0, j+1, i]); //back to blue
    }
    console.log('sorted:', array);
    return animations
}
