const PIVOT_COLOR = 'red';
const OVERWRITTE_COLOR = '#8000ff';
const FINAL_SORTED_COLOR = '#7dff84';
const ANIMATION_SPEED_MS = 10;
const arrayBars = document.getElementsByClassName('array-bar')

export async function doQuickSort(array) {
    console.log('array:', array);
    const start = 0;
    const end = array.length-1;
    quickSort(array, start, end);
    return array;
  }

async function quickSort(arr, start, end) {
    if (start >= end) {
        return;
    }
    let index = await partition(arr, start, end);
    await Promise.all([
        quickSort(arr, start, index-1), 
        quickSort(arr, index+1, end)
    ]);

}

async function partition(arr, start , end) {
    let pivotIndex = start;
    let pivotValue = arr[end];
    arrayBars[pivotIndex].style.backgroundColor = PIVOT_COLOR;
    for (let i = start; i < end; i++) {
        if (arr[i] < pivotValue) {
            await swap(arr, i, pivotIndex);
            pivotIndex++;
        }
    }
    await swap(arr, pivotIndex, end);
    return pivotIndex;
}

async function swap(arr, a, b) {

    // console.log('arrayBars swap:', arrayBars);
    // await sleep(10);
    let temp = arr[a];
    // arr[a] = arr[b];
    // arr[b] = temp;
    arrayBars[a].style.height = `${arr[b]}px`;
    arrayBars[b].style.height = `${temp}px`;
}

function sleep(ms) {
    return new Promise(resolve => setTimeout(resolve, ms));
}
