export function doInsertionSort(init_array) {
    const animations = [];
    console.log(array);
    const array = init_array.slice();
    for (let i=1; i<array.length; i++) {
        let key = array[i];
        let j = i-1;
        while (j>=0 && key<array[j]) {
            array[j + 1] = array[j];
            animations.push([2, j+1, j]);
            j-=1;
        }
        array[j+1]=key;
        animations.push([3, j+1, key]);
    }
    console.log('sorted:', array);
    return animations
}
